import axios from "axios"
const dataTables = [
    table = {
        name : 'User',
        columns : ['Id','Username', 'Name', 'Email', 'Phone', 'Address', 'Role', 'Status'],
        data : setDataByApi("/user/get/",localStorage.getItem("token") == null ? document.cookie : localStorage.getItem("token")) ,
        api_uri : {
            create : '/user/create',
            get : '/user/get/',
            update : '/user/update/',
            delete : '/user/delete/',
            paging : '/user/get-page/',
        }
    }
]
async function setDataByApi(api_uri,token){
    axios.get(api_uri + token).then(res => {
        return res.data;
    }).catch(err => {
        throw err;
    });
}
export default dataTables;
